package reactor;import reactor.evenhandler.handfactory.ServerListener;import java.util.HashMap;import java.util.Map;/** * 事件触发器 * Created by demopoo on 2017/8/17. */public class EvenHandlerFactory {    private static Map<String,ServerListener> map = map = new HashMap<String, ServerListener>();;    private static EvenHandlerFactory evenHandlerFactory = null;    /**     * 注册事件处理器     * @param serverListener     */    public static void addEvenHandler(String identy,ServerListener serverListener){        if (serverListener != null){            synchronized (map){                if (!map.containsKey(identy))                    map.put(identy,serverListener);            }        }    }    public static ServerListener getServerListener(String identy){        if (map.containsKey(identy)){            return map.get(identy);        }        throw new RuntimeException(String.format("sorry , not find the evenhandler:{}",identy));    }}