package reactor.evenhandler;import reactor.EvenHandlerFactory;import reactor.evenhandler.handfactory.EvenHandlerAdapter;import reactor.receive.HttpRequest;/** * Created by demopoo on 2017/8/16. */public class CloseHandler extends EvenHandlerAdapter {    @Override    public void OnClosed(HttpRequest httpRequest) {        System.out.println("onclosed");    }    @Override    public void afterPropertiesSet() throws Exception {        EvenHandlerFactory.addEvenHandler(this.getClass().getSimpleName(),this);    }}