package reactor.evenhandler;import reactor.EvenHandlerFactory;import reactor.evenhandler.handfactory.EvenHandlerAdapter;/** * Created by demopoo on 2017/8/16. */public class ErrorHandler extends EvenHandlerAdapter {    @Override    public void OnError(String errorMsg) {        System.out.println("onerror");    }    @Override    public void afterPropertiesSet() throws Exception {        EvenHandlerFactory.addEvenHandler(this.getClass().getSimpleName(),this);    }}