package reactor.evenhandler.handfactory;import reactor.Person;import reactor.receive.HttpRequest;import reactor.send.HttpResponse;import java.nio.channels.FileChannel;import java.nio.channels.SelectionKey;import java.nio.channels.Selector;import java.util.Iterator;import java.util.Map;/** * 服务器事件监听器 * Created by demopoo on 2017/8/16. */public interface ServerListener extends Runnable {    /**     * 服务端处理产生错误的时候触发该事件     * @param errorMsg     */    public void OnError(String errorMsg);    /**     * 当客户端发来（图片）数据已被服务器正确读取时，触发该事件     */    public void OnRead(Iterator<SelectionKey> sks, Selector selector, SelectionKey selectionKey, Map<SelectionKey,FileChannel> maps);    /**     * 当可以向客户端回复数据的时候触发     * @param httpRequest     * @param httpResponse     */    public void OnWriter(HttpRequest httpRequest, HttpResponse httpResponse);    /**     * 当客户端发来请求时候触发     */    public void OnAccept();    /**     * 当接收客户端请求后触发该事件     * @param httpRequest     */    public void OnAccepted(HttpRequest httpRequest);    /**     * 当客户端与服务端结束连接时触发     * @param httpRequest     */    public void OnClosed(HttpRequest httpRequest);}