package reactor.filechannel;import java.io.IOException;import java.nio.channels.FileChannel;import java.nio.channels.SelectionKey;import java.nio.file.Paths;import java.nio.file.StandardOpenOption;import java.util.Map;import java.util.Random;import java.util.concurrent.Callable;/** * Created by demopoo on 2017/8/26. */public class CreateFileChannel implements Callable {    public Object call() throws Exception {        try {            Random random = new Random();            FileChannel fileChannel = FileChannel.open(Paths.get("/Users/demopoo/Desktop/"+System.currentTimeMillis()+""+random.nextInt(1000)+".png"), StandardOpenOption.WRITE,StandardOpenOption.CREATE);            return fileChannel;        }catch (IOException ex){            ex.printStackTrace();            return null;        }    }}