package reactor.receive;import java.net.InetAddress;import java.net.SocketException;import java.nio.channels.SocketChannel;/** * Created by demopoo on 2017/8/16. */public class HttpRequest {    private byte[] recData;    private SocketChannel socketChannel;    public  HttpRequest(SocketChannel socketChannel){        this.socketChannel = socketChannel;    }    public InetAddress getInetAddress(){        return this.socketChannel.socket().getInetAddress();    }    public int getPort(){        return this.socketChannel.socket().getPort();    }    public boolean isConnected(){        return this.socketChannel.isConnected();    }    public boolean isBlocking(){        return this.socketChannel.isBlocking();    }    public boolean getKeepAlive(){        try {            return this.socketChannel.socket().getKeepAlive();        }catch (SocketException ex){            return true;        }    }    public byte[] getRecData() {        return recData;    }    public void setRecData(byte[] recData) {        this.recData = recData;    }    public SocketChannel getSocketChannel() {        return socketChannel;    }    public void setSocketChannel(SocketChannel socketChannel) {        this.socketChannel = socketChannel;    }}