package reactor.send;import java.io.IOException;import java.nio.ByteBuffer;import java.nio.channels.SocketChannel;/** * Created by demopoo on 2017/8/16. */public class HttpResponse {    private SocketChannel socketChannel;    public HttpResponse(SocketChannel socketChannel){        this.socketChannel = socketChannel;    }    /**     * 向客户端写数据     * @param bytes 回复数据内容     */    public void send(byte[] bytes){        try {            ByteBuffer byteBuffer = ByteBuffer.allocate(bytes.length);            byteBuffer.put(bytes);            byteBuffer.flip();            socketChannel.write(byteBuffer);            byteBuffer.clear();        }catch (IOException ex){            ex.printStackTrace();        }finally {            try {                socketChannel.close();            }catch (IOException ex){                ex.printStackTrace();            }        }    }}