package reactor.session;import directbytebuffer.DirectByteBufferPool;import java.nio.ByteBuffer;import java.nio.channels.SelectionKey;import java.nio.channels.SocketChannel;/** * Created by demopoo on 2017/9/4. * 客户端连接会话管理 */public class ConnectSession {    private boolean curOwner = true;    private DirectByteBufferPool directByteBufferPool;    private SocketChannel socketChannel;    private SelectionKey selectionKey;    public ConnectSession(SocketChannel socketChannel,SelectionKey selectionKey){        ByteBuffer byteBuffer = ByteBuffer.allocateDirect(1024*10);        this.directByteBufferPool = new DirectByteBufferPool(byteBuffer);        this.socketChannel = socketChannel;        this.selectionKey = selectionKey;    }    public boolean isCurOwner() {        return curOwner;    }    public void setCurOwner(boolean curOwner) {        this.curOwner = curOwner;    }    public DirectByteBufferPool getDirectByteBufferPool() {        return directByteBufferPool;    }    public void setDirectByteBufferPool(DirectByteBufferPool directByteBufferPool) {        this.directByteBufferPool = directByteBufferPool;    }    public SocketChannel getSocketChannel() {        return socketChannel;    }    public void setSocketChannel(SocketChannel socketChannel) {        this.socketChannel = socketChannel;    }    public SelectionKey getSelectionKey() {        return selectionKey;    }    public void setSelectionKey(SelectionKey selectionKey) {        this.selectionKey = selectionKey;    }}