package reactor.threadpool;import java.util.concurrent.ExecutorService;import java.util.concurrent.Executors;/** * Created by demopoo on 2017/8/17. */public class ThreadPoolCommen {    private static int maxPoolSize = 500;    private static ExecutorService executorService;    public static ExecutorService achieveThread(){        if (executorService ==null){            synchronized (threadpool.ThreadPoolCommen.class){                executorService = Executors.newFixedThreadPool(maxPoolSize);            }        }        return executorService;    }}