package reactormode.handle;import reactormode.sessions.Session;import java.io.IOException;import java.nio.channels.SelectionKey;import java.nio.channels.Selector;/** * Created by demopoo on 2017/9/9. */public interface NIOHandle<T extends Session> {    void onSocketRead(T session) throws IOException;    void onSocketWrite(T session)  throws IOException;    void onSocketClose(Selector selector,SelectionKey selectionKey)  throws IOException;    void onSocketConnection(Selector selector,SelectionKey selectionKey)  throws IOException;}