package threadpool;import java.util.concurrent.Executor;import java.util.concurrent.ExecutorService;import java.util.concurrent.Executors;/** * Created by demopoo on 2017/8/2. */public class ThreadPoolCommen {    private static int maxPoolSize = 50;    private static ExecutorService executorService;    public static ExecutorService achieveThread(){        if (executorService ==null){            synchronized (ThreadPoolCommen.class){                executorService = Executors.newFixedThreadPool(maxPoolSize);            }        }        return executorService;    }}