package bytebuffer;import org.apache.kafka.clients.producer.*;import java.util.Properties;/** * Created by demopoo on 2017/9/11. */public class kafka {    public static void main(String[] args){        Properties props = new Properties();        props.put("bootstrap.servers", "10.50.100.81:9092");        props.put("acks", "all");        props.put("retries", 0);        props.put("batch.size", 16384);        props.put("linger.ms", 1);        props.put("buffer.memory", 33554432);        props.put("key.serializer", "org.apache.kafka.common.serialization.StringSerializer");        props.put("value.serializer", "org.apache.kafka.common.serialization.StringSerializer");        Producer<String, String> producer = new KafkaProducer<String, String>(props);        for (int i = 0; i < 100; i++)            producer.send(new ProducerRecord<String, String>("demopoo", Integer.toString(i), Integer.toString(i)), new Callback() {                public void onCompletion(RecordMetadata recordMetadata, Exception e) {                    System.out.println(1);                }            });        producer.close();    }}